import React, { useEffect, useState } from 'react';
import axios from '../api/axios';
import { useParams } from 'react-router-dom';

const UserProfilePage = () => {
  const { username } = useParams();
  const currentUser = localStorage.getItem('username');
  const [posts, setPosts] = useState([]);
  const [followStatus, setFollowStatus] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('access');

        const postsRes = await axios.get(`profile/${username}/`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setPosts(postsRes.data);

        if (username !== currentUser) {
          const statusRes = await axios.get(`follow-status/${username}/`, {
            headers: { Authorization: `Bearer ${token}` },
          });
          setFollowStatus(statusRes.data.is_following);
        }
      } catch (err) {
        console.error(err);
      }
    };

    fetchData();
  }, [username, currentUser]);

  const toggleFollow = async () => {
    try {
      const token = localStorage.getItem('access');
      const res = await axios.post(`follow/${username}/`, {}, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFollowStatus((prev) => !prev);
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div>
      <h2>Profile: {username}</h2>
      {followStatus !== null && (
        <button onClick={toggleFollow}>
          {followStatus ? 'Unfollow' : 'Follow'}
        </button>
      )}
      {posts.map((post) => (
        <div key={post.id}>
          <p>{post.caption}</p>
          <img src={post.image} alt="post" width="300" />
        </div>
      ))}
    </div>
  );
};

export default UserProfilePage;
