// src/pages/ProfilePage.jsx
import React, { useEffect, useState } from 'react';
import axios from '../api/axios';
import { useParams } from 'react-router-dom';

const ProfilePage = () => {
  const { username } = useParams();
  const currentUser = localStorage.getItem('username');
  const [posts, setPosts] = useState([]);
  const [followStatus, setFollowStatus] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchUserPosts = async () => {
      try {
        const token = localStorage.getItem('access');

        const postsRes = await axios.get(`profile/${username}/`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        setPosts(postsRes.data);

        if (username !== currentUser) {
          const followCheck = await axios.post(`follow/${username}/`, {}, {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          });
          setFollowStatus(followCheck.data.message === 'Followed' ? true : false);
        }

      } catch (err) {
        console.error(err.response?.data || err.message);
        setError('Error loading profile');
      }
    };

    fetchUserPosts();
  }, [username, currentUser]);

  const toggleFollow = async () => {
    try {
      const token = localStorage.getItem('access');
      const res = await axios.post(`follow/${username}/`, {}, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (res.data.message === 'Followed') {
        setFollowStatus(true);
      } else if (res.data.message === 'Unfollowed') {
        setFollowStatus(false);
      }
    } catch (err) {
      console.error(err.response?.data || err.message);
    }
  };

  return (
    <div className="profile-page">
      <h2>@{username}'s Posts</h2>
      {username !== currentUser && (
        <button onClick={toggleFollow}>
          {followStatus ? 'Unfollow' : 'Follow'}
        </button>
      )}
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {posts.length === 0 ? (
        <p>No posts yet.</p>
      ) : (
        posts.map((post) => (
          <div key={post.id} className="post-card">
            <img src={post.image} alt="user post" style={{ width: '100%', maxWidth: '400px' }} />
            <p>{post.caption}</p>
            <small>{new Date(post.created_at).toLocaleString()}</small>
          </div>
        ))
      )}
    </div>
  );
};

export default ProfilePage;
