// src/pages/FeedPage.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import "../styles/feed.css"; // optional styling

const FeedPage = () => {
  const [posts, setPosts] = useState([]);
  const token = localStorage.getItem("accessToken");

  useEffect(() => {
    axios
      .get("http://127.0.0.1:8000/api/posts/", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((res) => setPosts(res.data))
      .catch((err) => console.error("Error loading posts:", err));
  }, []);

  const handleLike = (postId) => {
    axios
      .post(`http://127.0.0.1:8000/api/posts/${postId}/like/`, {}, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((res) => alert(res.data.message))
      .catch((err) => console.error("Like error:", err.response?.data));
  };

  return (
    <div className="feed-container">
      <h2>üì∏ Recent Posts</h2>
      {posts.map((post) => (
        <div key={post.id} className="post-card">
          <img src={post.image} alt="Post" className="post-image" />
          <h4>@{post.user}</h4>
          <p>{post.caption}</p>
          <small>{new Date(post.created_at).toLocaleString()}</small>
          <br />
          <button onClick={() => handleLike(post.id)}>‚ù§Ô∏è Like</button>
        </div>
      ))}
    </div>
  );
};

export default FeedPage;
