import React, { useEffect, useState } from 'react';
import axios from '../api/axios';
import { Link } from 'react-router-dom';

const UserProfilePage = () => {
  const [users, setUsers] = useState([]);
  const token = localStorage.getItem('access');

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await axios.get('users/', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUsers(response.data);
      } catch (err) {
        console.error('Error fetching users:', err.response?.data || err.message);
      }
    };

    fetchUsers();
  }, [token]);

  const toggleFollow = async (username) => {
    try {
      const response = await axios.post(`follow/${username}/`, {}, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(prev =>
        prev.map(user =>
          user.username === username
            ? { ...user, is_following: !user.is_following }
            : user
        )
      );
    } catch (err) {
      console.error('Error following/unfollowing:', err.response?.data || err.message);
    }
  };

  return (
    <div className="user-list-page">
      <h2>All Users</h2>
      {users.length === 0 ? (
        <p>No other users found.</p>
      ) : (
        <ul>
          {users.map(user => (
            <li key={user.username} style={{ marginBottom: '12px' }}>
              <Link to={`/profile/${user.username}`} style={{ fontWeight: 'bold' }}>
                @{user.username}
              </Link>
              <button
                onClick={() => toggleFollow(user.username)}
                style={{
                  marginLeft: '12px',
                  padding: '4px 10px',
                  cursor: 'pointer',
                  backgroundColor: user.is_following ? '#ccc' : '#4caf50',
                  color: '#fff',
                  border: 'none',
                  borderRadius: '4px'
                }}
              >
                {user.is_following ? 'Unfollow' : 'Follow'}
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default UserProfilePage;
