import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Navbar = () => {
  const navigate = useNavigate();
  const access = localStorage.getItem('access');
  const username = localStorage.getItem('username'); // assuming you store it after login

  const handleLogout = () => {
    localStorage.removeItem('access');
    localStorage.removeItem('refresh');
    localStorage.removeItem('username');
    navigate('/login');
  };

  return (
    <nav style={styles.nav}>
      <h2 style={styles.logo}>SocialApp</h2>
      {access ? (
        <div style={styles.links}>
          <Link to="/feed" style={styles.link}>Feed</Link>
          <Link to="/create" style={styles.link}>Create</Link>
          <Link to={`/profile/${username}`} style={styles.link}>Profile</Link>
          <Link to="/my-profile" style={styles.link}>Profile</Link>
          <button onClick={handleLogout} style={styles.logout}>Logout</button>
        </div>
      ) : (
        <div style={styles.links}>
          <Link to="/login" style={styles.link}>Login</Link>
          <Link to="/register" style={styles.link}>Register</Link>
        </div>
      )}
    </nav>
  );
};

const styles = {
  nav: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '12px 24px',
    backgroundColor: '#282c34',
    color: '#fff',
  },
  logo: {
    margin: 0,
  },
  links: {
    display: 'flex',
    gap: '20px',
    alignItems: 'center',
  },
  link: {
    color: '#fff',
    textDecoration: 'none',
    fontWeight: 'bold',
  },
  logout: {
    backgroundColor: '#ff4d4f',
    color: '#fff',
    border: 'none',
    padding: '6px 12px',
    cursor: 'pointer',
    borderRadius: '4px',
  },
};

export default Navbar;
