import React, { useEffect, useState } from 'react';
import axios from '../api/axios';
import { Link } from 'react-router-dom';

const UserProfilePage = () => {
  const [users, setUsers] = useState([]);
  const [followed, setFollowed] = useState({});
  const currentUser = localStorage.getItem('username');
  const token = localStorage.getItem('access');

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const res = await axios.get('/users/', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setUsers(res.data.filter(user => user.username !== currentUser));
      } catch (err) {
        console.error('Failed to fetch users:', err);
      }
    };

    const fetchFollowing = async () => {
      try {
        const res = await axios.get(`/profile/${currentUser}/`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        const followingUsernames = res.data.following.map(f => f.username);
        const followMap = {};
        followingUsernames.forEach(username => {
          followMap[username] = true;
        });
        setFollowed(followMap);
      } catch (err) {
        console.error('Failed to fetch following:', err);
      }
    };

    fetchUsers();
    fetchFollowing();
  }, [currentUser, token]);

  const handleToggleFollow = async (username) => {
    try {
      await axios.post(`/follow/${username}/`, {}, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setFollowed(prev => ({
        ...prev,
        [username]: !prev[username],
      }));
    } catch (err) {
      console.error('Follow/unfollow error:', err);
    }
  };

  return (
    <div style={styles.container}>
      <h2>All Users</h2>
      {users.map(user => (
        <div key={user.username} style={styles.userCard}>
          <Link to={`/profile/${user.username}`} style={styles.username}>
            @{user.username}
          </Link>
          <button onClick={() => handleToggleFollow(user.username)} style={styles.followBtn}>
            {followed[user.username] ? 'Unfollow' : 'Follow'}
          </button>
        </div>
      ))}
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
  },
  userCard: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '12px',
    borderBottom: '1px solid #ddd',
    paddingBottom: '8px',
  },
  username: {
    textDecoration: 'none',
    color: '#2c3e50',
    fontWeight: 'bold',
  },
  followBtn: {
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    padding: '6px 12px',
    cursor: 'pointer',
    borderRadius: '4px',
  },
};

export default UserProfilePage;
